import React from 'react';
import './styles.css'
import Heading from "./Components/Heading"
import Die from './Components/Die';
import RollButton from "./Components/Button"

function App() {

  const [diceNumbers, setDiceNumbers] = React.useState(randomNumber)

  function randomNumber() {
    let dieNumber = []
    let arr = 0

    while(arr < 10) {
      dieNumber.push(Math.floor(Math.random() * 6 + 1));
      arr++;
    }
    return dieNumber
  }

  const makeDice = diceNumbers.map(diceNumber => <Die number={diceNumber}/>)

  function rollDice() {
    setDiceNumbers(randomNumber)
  }

  return (
    <main>
      <Heading />
      <div className="dice-container">{makeDice}</div>
      <RollButton roll="Roll" click={rollDice}/>

    </main>
  )
}

export default App;
