import React, { useState } from 'react';
import './styles.css'
import Heading from "./Components/Heading"
import { nanoid } from 'nanoid';
import Die from './Components/Die';
import RollButton from "./Components/Button"

function App() {

  const [diceNumbers, setDiceNumbers] = useState(randomNumber)

  function randomNumber() {
    let dieNumbers = []
    for (let i = 0; i < 10; i++) {
      dieNumbers.push({
        key: nanoid(),
        value: Math.floor(Math.random() *6 + 1),
        isHeld: false
      })

    }
    console.log(dieNumbers[0].value)
    return dieNumbers
  }


  const makeDice = diceNumbers.map(diceNumber => <Die number={diceNumber}/>)

  function rollDice() {
    setDiceNumbers(randomNumber)
  }

  return (
    <main>
      <Heading value={diceNumbers}/>
      <div className="dice-container">{makeDice}</div>
      <RollButton roll="Roll" click={rollDice}/>

    </main>
  )
}

export default App;
