import React, { useState } from 'react';
import './styles.css'
import Heading from "./Components/Heading"
import DieGroup from './Components/DieGroup';
import RollButton from "./Components/Button"

function App() {

  function randomNumber() {
    let dieNumber = [];
    //let random = Math.floor(Math.random() * 6 + 1)
    let arr = 0

    while(arr < 10) {
      dieNumber.push(Math.floor(Math.random() * 6 + 1))
      arr++
    }
    console.log(dieNumber)
  }

  return (
    <main>
      <Heading onClick={randomNumber}/>
      <DieGroup number="1"/>
      <RollButton roll="Roll" />

    </main>
  )
}

export default App;
